import React, { Component, Fragment, useEffect, useState } from "react";
import styled from "styled-components";
import { SURVEY_DATA, AWS_API } from "../../config";
import { flexCenter, boxShadow } from "../../styles/CommonStyle";

const Survey = () => {
  const [progress, setProgress] = useState();
  const [survey, setSurvey] = useState();

  useEffect(() => {
    fetch(`${AWS_API}/survey/start`, {
      method: "GET",
      headers: {
        Authorization: localStorage.getItem("token"),
      },
    })
      .then((res) => res.json())
      .then((result) => {
        setProgress(result.progress);
        setSurvey(result.survey);
      });
  }, []);

  // const Progress = progress && progress;

  // const postAnswerA = () => {
  //   fetch(`AWS_API/survey/input`, {
  //     method: "POST",
  //     header: {
  //       Authoriztion: localStorage.getItem("token"),
  //     },
  //     body: JSON.stringify({
  //       survey_id: surveyId,
  //       answer: "A",
  //     }),
  //   })
  //     .then((res) => res.json())
  //     .then((res) => console.log(res));
  // };

  // const postAnswerB = () => {
  //   fetch(`AWS_API/survey/input`, {
  //     method: "POST",
  //     headers: {
  //       Authoriztion: localStorage.getItem("token"),
  //     },
  //     body: JSON.stringify({
  //       survey_id: surveyId,
  //       answer: "B",
  //     }),
  //   })
  //     .then((res) => res.json())
  //     .then((res) => console.log(res));
  // };

  const progressCurrent = progress && progress.current;
  const progressTotal = progress && progress.total;

  const surveyId = survey && survey.id;
  const surveyContent = survey && survey.content;
  // const Question = surveyContent && surveyContent.slice(0, surveyContent.indexOf("A."));

  const questionIdx = surveyContent && surveyContent.indexOf("A");
  Math.floor(surveyContent && surveyContent.indexOf("A"));
  // console.log(surveyContent.slice(0, questionIdx));

  return (
    <Fragment>
      <WrapSurvey>
        <Header>
          <span>핵심 능력 테스트</span>
          <span>
            {progressCurrent}/{progressTotal}{" "}
          </span>
        </Header>
        <WrapMain>
          <Question>
            <h1>
              <div className="emphasis">{}</div>
            </h1>
          </Question>
          <Answer>
            <Button>{surveyContent}</Button>
            <p>vs</p>
            <Button></Button>
          </Answer>
        </WrapMain>
      </WrapSurvey>
    </Fragment>
  );
};

export default Survey;

const WrapSurvey = styled.section`
  ${flexCenter};
  flex-direction: column;
`;

const Header = styled.section`
  display: flex;
  justify-content: space-between;
  width: 360px;
  padding: 170px 24px 0 24px;
  color: rgba(0, 0, 0, 0.4);
  font-size: 15px;
`;

const WrapMain = styled.section`
  padding-top: 100px;
`;

const Question = styled.div`
  text-align: center;
  font-size: 30px;
  h1 {
    .emphasis {
      color: #632ed8;
      font-weight: bold;
      width: 360px;
    }
  }
`;

const Answer = styled.div`
  ${flexCenter};
  flex-direction: column;
  padding-top: 80px;

  p {
    color: rgba(0, 0, 0, 0.4);
    padding: 20px 0;
    font-size: 24px;
  }
`;

const Button = styled.button`
  width: 360px;
  padding: 30px 50px;
  border: 1px solid rgba(0, 0, 0, 0.3);
  border-radius: 50px;
  font-size: 16px;
  outline-style: none;

  &:hover {
    ${boxShadow}
    cursor: pointer;
    transform: scale(1.05);
    transition: 0.2s;
  }
`;
