import React, { Component, Fragment, useEffect, useState } from "react";
import styled from "styled-components";
import { SURVEY_DATA, AWS_API } from "../../config";
import { flexCenter, boxShadow } from "../../styles/CommonStyle";

const Survey = () => {
  const [data, setData] = useState([]);

  // useEffect(() => {
  //   fetch(`${AWS_API}/survey/start`, {
  //     method: "GET",
  //     header: {
  //       Authoriztion: localStorage.getItem("token"),
  //     },
  //   })
  //     .then((res) => res.json())
  //     .then((res) => console.log(res));
  // }, []);

  useEffect(() => {
    fetch(SURVEY_DATA)
      .then((res) => res.json())
      .then((res) => setData(res.surveyQuestion));
  }, []);

  console.log(data);

  const changeBtnColor = () => {};
  const q1 = data.q1 && data.q1.question;
  return (
    <Fragment>
      <WrapSurvey>
        <Header>
          <span>핵심 능력 테스트</span>
          <span>{data.q1 && data.q1.id}</span>
        </Header>
        <WrapMain>
          <Question>
            <h1>
              <div className="emphasis">{data.q1 && q1.slice(0, q1.indexOf("\r"))}</div>
            </h1>
          </Question>
          <Answer>
            <Button onClick={postAnswerA}>
              {data.q1 && q1.slice(q1.indexOf("\r"), q1.lastIndexOf("\r"))}
            </Button>
            <p>vs</p>
            <Button onClick={postAnswerB}>{data.q1 && q1.slice(q1.lastIndexOf("\r"))}</Button>
          </Answer>
        </WrapMain>
      </WrapSurvey>
    </Fragment>
  );
};

export default Survey;

const WrapSurvey = styled.section`
  ${flexCenter};
  flex-direction: column;
`;

const Header = styled.section`
  display: flex;
  justify-content: space-between;
  width: 360px;
  padding: 170px 24px 0 24px;
  color: rgba(0, 0, 0, 0.4);
  font-size: 15px;
`;

const WrapMain = styled.section`
  padding-top: 100px;
`;

const Question = styled.div`
  text-align: center;
  font-size: 30px;
  h1 {
    .emphasis {
      color: #632ed8;
      font-weight: bold;
      width: 360px;
    }
  }
`;

const Answer = styled.div`
  ${flexCenter};
  flex-direction: column;
  padding-top: 80px;

  p {
    color: rgba(0, 0, 0, 0.4);
    padding: 20px 0;
    font-size: 24px;
  }
`;

const Button = styled.button`
  width: 360px;
  padding: 30px 50px;
  border: 1px solid rgba(0, 0, 0, 0.3);
  border-radius: 50px;
  font-size: 16px;
  outline-style: none;

  &:hover {
    cursor: pointer;
    transform: scale(1.05);
    ${boxShadow}
    transition: 0.2s;
  }
`;
