import React, { useState, useEffect } from "react";
import styled from "styled-components";
import { theme } from "../../../styles/CommonStyle";
import { useHistory } from "react-router-dom";

const Nav = () => {
  const [login, setLogin] = useState(false);

  const isLogin = localStorage.getItem("token");

  useEffect(() => {
    if (isLogin) {
      setLogin(true);
    } else {
      setLogin(false);
    }
  });

  const history = useHistory();
  const goToLogout = () => {
    localStorage.removeItem("token");
    history.push("/Main");
  };

  const goToSignIn = () => {
    history.push("/SignIn");
  };

  const goToSignUp = () => {
    history.push("/Certification");
  };

  return (
    <NavBar>
      <Container>
        {login ? (
          <Login onClick={goToLogout}>로그아웃</Login>
        ) : (
          <Login onClick={goToSignIn}>로그인</Login>
        )}
        <SignUp onClick={goToSignUp}>회원가입</SignUp>
      </Container>
    </NavBar>
  );
};

const NavBar = styled.div`
  display: flex;
  width: 100%;
  padding: 0 20px;
  z-index: 100;
  /* position: fixed; */
  background: #ffffff;
  border-bottom: 10px solid #dfdfdf;
  /* position: relative; */
`;

const Container = styled.div`
  display: flex;
  position: absolute;
  right: 50px;
  /* justify-content: center; */
  margin: 15px 0;
`;

const Login = styled.div`
  padding-top: 7px;
  color: #222;
  font-size: 17px;
  font-weight: 700;
  margin: 0 10px;

  &:hover {
    cursor: pointer;
    width: 80px;
    height: 30px;
    border: 1px solid pink;
    border-radius: 15px;
    color: #fff;
    background-color: ${theme.pink};
  }
`;

const SignUp = styled.div`
  padding-top: 7px;
  color: #222;
  font-size: 17px;
  font-weight: 700;
  margin: 0 10px;

  &:hover {
    cursor: pointer;
    width: 80px;
    height: 30px;
    border: 1px solid pink;
    border-radius: 15px;
    color: #fff;
    background-color: ${theme.pink};
  }
`;

export default Nav;
