import React from "react";
import styled from "styled-components";

const FOOTERLIST = [
  {
    id: 0,
    name: "회사소개",
  },
  {
    id: 1,
    name: "채용정보",
  },
  {
    id: 2,
    name: "이용약관",
  },
  {
    id: 3,
    name: "개인정보처리방침",
  },
  {
    id: 4,
    name: "공지사함",
  },
  {
    id: 5,
    name: "고객센터",
  },
];

const Footer = () => {
  return (
    <Wrapper>
      <Container>
        <Top>
          <CustomerCenter>문의</CustomerCenter>
          <Telephone>1234-5678</Telephone>
          <Date>10:00 ~ 18:00 (주말 & 공휴일 제외)</Date>
        </Top>
        <FooterList>
          {FOOTERLIST.map((list) => {
            return <div className="listName">{list.name}</div>;
          })}
        </FooterList>
      </Container>
    </Wrapper>
  );
};

const Container = styled.div`
  padding: 30px 60px 40px;
  margin: 0 auto;
`;

const FooterList = styled.div`
  display: flex;
  margin-top: 20px;

  .listName {
    height: 20px;
    margin: 0 3px;
    padding: 2px;
    color: #424242;
    text-decoration: none;
    font-size: 15px;
    font-weight: 700;
    line-height: 16px;

    &:hover {
      cursor: pointer;
    }
  }
`;

const Wrapper = styled.div`
  width: 100%;
  bottom: 0;
  position: relative;
  display: flex;
  background-color: #fafafa;
  z-index: 100;
  margin-bottom: 0;
`;

const Top = styled.div`
  margin: 0 auto;
  display: flex;
  flex-direction: column;
`;

const CustomerCenter = styled.div`
  font-weight: bold;
  color: #424242;
  text-decoration: none;
  font-size: 12px;
  line-height: 16px;

  &:hover {
    cursor: pointer;
  }
`;

const Telephone = styled.div`
  margin: 2px 0 0;
  color: #424242;
  font-size: 30px;
  font-weight: 700;
  line-height: 36px;
`;

const Date = styled.div`
  color: #757575;
  font-size: 12px;
  line-height: 16px;
  letter-spacing: -0.4px;

  &:hover {
    cursor: pointer;
  }
`;

export default Footer;
