/* eslint-disable no-const-assign */
/* eslint-disable eqeqeq */
import { cleanup } from "@testing-library/react";
import React, { Fragment, useEffect, useState } from "react";
import { useHistory } from "react-router-dom";
import styled from "styled-components";
import { AWS_API } from "../../config";
import { flexCenter, boxShadow } from "../../styles/CommonStyle";

const Survey = () => {
  const history = useHistory();
  const [progress, setProgress] = useState();
  const [survey, setSurvey] = useState();
  const [oldTime, setOldTime] = useState(0);
  const [currentTime, setCurrentTime] = useState(0);
  const [testTime, setTestTime] = useState(0);

  const progressCurrent = progress && progress.current;
  const progressTotal = progress && progress.total;
  const surveyId = survey && survey.id;
  const surveyContent = survey && survey.content;
  const questionIdx = surveyContent && surveyContent.indexOf("A");
  const firstAnswer = surveyContent && surveyContent.indexOf("B");

  // console.log(+currentTime - +oldTime);

  const countTime = () => {
    let getTime = new Date().getSeconds();
    return getTime;
  };

  const firstFetch = () => {
    fetch(`${AWS_API}/survey/start`, {
      method: "GET",
      headers: {
        Authorization: localStorage.getItem("token"),
      },
    })
      .then((res) => res.json())
      .then((result) => {
        setProgress(result.progress);
        setSurvey(result.survey);
      });
  };

  useEffect(() => {
    firstFetch();
    return () => {
      setOldTime(countTime());
      console.log(oldTime);
    };
  }, [survey]);

  const postAnswerA = () => {
    fetch(`${AWS_API}/survey/input`, {
      method: "POST",
      headers: {
        Authorization: localStorage.getItem("token"),
      },
      body: JSON.stringify({
        survey_id: survey && survey.id,
        answer: "A",
        time: 20,
      }),
    })
      .then((res) => res.json())
      .then((res) => {
        setCurrentTime(countTime());
        if (res.survey.survey.id > 6) {
          history.push("/Result");
        }
      });
  };

  const postAnswerB = () => {
    fetch(`${AWS_API}/survey/input`, {
      method: "POST",
      headers: {
        Authorization: localStorage.getItem("token"),
      },
      body: JSON.stringify({
        survey_id: surveyId,
        answer: "B",
        time: 20,
      }),
    })
      .then((res) => res.json())
      .then((res) => console.log(res));
  };

  const postSkip = () => {
    fetch(`${AWS_API}/survey/input`, {
      method: "POST",
      headers: {
        Authorization: localStorage.getItem("token"),
      },
      body: JSON.stringify({
        survey_id: surveyId,
        answer: "Skip",
        time: 20,
      }),
    })
      .then((res) => res.json())
      .then((res) => console.log(res));
  };

  const resetTest = () => {
    fetch(`${AWS_API}/survey/reset`, {
      method: "POST",
      headers: {
        Authorization: localStorage.getItem("token"),
      },
    })
      .then((res) => res.json())
      .then((res) => console.log(res));
  };

  return (
    <Fragment>
      <WrapSurvey>
        <SurveyBackground>
          <Header>
            <span>핵심 능력 테스트</span>
            <span>
              {progressCurrent}/{progressTotal}
            </span>
          </Header>
          <WrapMain>
            <Question>
              <h1>
                <div className="emphasis">{survey && surveyContent.slice(0, questionIdx)}</div>
              </h1>
            </Question>
            <Answer>
              <Button onClick={postAnswerA}>
                {survey && surveyContent.slice(questionIdx, firstAnswer)}
              </Button>
              <p>vs</p>
              <Button onClick={postAnswerB}>{survey && surveyContent.slice(firstAnswer)}</Button>
              <div className="WrapBottomButton">
                <SkipButton onClick={postSkip}>Skip</SkipButton>
                <Retest onClick={resetTest}>처음으로</Retest>
              </div>
            </Answer>
          </WrapMain>
          <LogoImage src="https://www.dneuro.es/wp-content/uploads/2017/07/logo-para-fondo-blanco-120x400.png" />
        </SurveyBackground>
      </WrapSurvey>
    </Fragment>
  );
};

export default Survey;

const WrapSurvey = styled.section`
  ${flexCenter};
  flex-direction: column;
`;

const SurveyBackground = styled.div`
  height: 100vh;
  padding: 0 40px;
  background-color: rgba(245, 245, 219, 0.4);
`;

const Header = styled.section`
  display: flex;
  justify-content: space-between;
  padding: 80px 50px 0;
  color: rgba(0, 0, 0, 0.4);
  font-size: 14px;
  font-weight: bold;
`;

const WrapMain = styled.section`
  padding-top: 80px;
`;

const Question = styled.div`
  font-size: 20px;
  text-align: center;

  h1 {
    .emphasis {
      width: 380px;
      padding: 35px;
      border-radius: 50px;
      color: #fff;
      background-color: rgba(191, 204, 0, 0.8);
      font-weight: bold;
      line-height: 30px;
    }
  }
`;

const Answer = styled.div`
  ${flexCenter};
  flex-direction: column;
  padding-top: 80px;

  p {
    padding: 10px 0;
    color: rgba(0, 0, 0, 0.4);
    font-size: 24px;
  }
`;

const Button = styled.button`
  width: 360px;
  padding: 30px 50px;
  border: 2px solid rgba(0, 0, 0, 0.08);
  border-radius: 50px;
  font-size: 14px;
  font-weight: bold;
  outline-style: none;

  &:hover {
    ${boxShadow}
    cursor: pointer;
    transform: scale(1.1);
    transition: 0.3s;
    border: none;
    color: #fff;
    background-color: rgba(191, 204, 0, 0.7);
  }
`;

const SkipButton = styled.button`
  width: 100px;
  margin: 80px 10px 0 20px;
  padding: 10px 20px;
  border: none;
  border-radius: 50px;
  background-color: #becc01;
  color: #fff;
  font-size: 14px;
  font-weight: 500px;
  outline-style: none;

  &:hover {
    ${boxShadow}
    cursor: pointer;
    transform: scale(1.1);
    transition: 0.2s;
  }
`;

const Retest = styled(SkipButton.withComponent("button"))``;

const LogoImage = styled.img`
  width: 200px;
  height: 60px;
  margin: auto;
  margin-top: 50px;
`;
