import React, { Fragment, useEffect, useState } from "react";
import styled from "styled-components";
import { AWS_API } from "../../config";
import { flexCenter, boxShadow } from "../../styles/CommonStyle";

const Survey = () => {
  const [progress, setProgress] = useState();
  const [survey, setSurvey] = useState();
  const [newData, setNewData] = useState();

  const progressCurrent = progress && progress.current;
  const progressTotal = progress && progress.total;
  const surveyId = survey && survey.id;
  const surveyContent = survey && survey.content;
  const questionIdx = surveyContent && surveyContent.indexOf("A");
  const firstAnswer = surveyContent && surveyContent.indexOf("B");
  const old = new Date();

  console.log(surveyId);

  useEffect(() => {
    fetch(`${AWS_API}/survey/start`, {
      method: "GET",
      headers: {
        Authorization: localStorage.getItem("token"),
      },
    })
      .then((res) => res.json())
      .then((result) => {
        setProgress(result.progress);
        setSurvey(result.survey);
      });
  }, []);

  const postAnswerA = () => {
    fetch(`${AWS_API}/survey/input`, {
      method: "POST",
      headers: {
        Authorization: localStorage.getItem("token"),
      },
      body: JSON.stringify({
        survey_id: survey && survey.id,
        answer: "A",
        time: 20,
      }),
    })
      .then((res) => res.json())
      .then((res) => setNewData(res));
  };

  const postAnswerB = () => {
    fetch(`${AWS_API}/survey/input`, {
      method: "POST",
      headers: {
        Authorization: localStorage.getItem("token"),
      },
      body: JSON.stringify({
        survey_id: surveyId,
        answer: "B",
        time: 20,
      }),
    })
      .then((res) => res.json())
      .then((res) => setNewData(res));
  };

  const postSkip = () => {
    fetch(`${AWS_API}/survey/input`, {
      method: "POST",
      headers: {
        Authorization: localStorage.getItem("token"),
      },
      body: JSON.stringify({
        survey_id: surveyId,
        answer: "Skip",
        time: 20,
      }),
    })
      .then((res) => res.json())
      .then((res) => setNewData(res));
  };

  return (
    <Fragment>
      <WrapSurvey>
        <SurveyBackground>
          <Header>
            <span>핵심 능력 테스트</span>
            <span>
              {progressCurrent}/{progressTotal}
            </span>
          </Header>
          <WrapMain>
            <Question>
              <h1>
                <div className="emphasis">{survey && surveyContent.slice(0, questionIdx)}</div>
              </h1>
            </Question>
            <Answer>
              <Button onClick={postAnswerA}>
                {survey && surveyContent.slice(questionIdx, firstAnswer)}
              </Button>
              <p>vs</p>
              <Button onClick={postAnswerB}>{survey && surveyContent.slice(firstAnswer)}</Button>
              <WrapBottomButton>
                <SkipButton onClick={postSkip}>Skip</SkipButton>
                <Retest>다시하기</Retest>
              </WrapBottomButton>
            </Answer>
          </WrapMain>
        </SurveyBackground>
      </WrapSurvey>
    </Fragment>
  );
};

export default Survey;

const WrapSurvey = styled.section`
  ${flexCenter};
  flex-direction: column;
`;

const SurveyBackground = styled.div`
  height: 100vh;
  padding: 0 40px;
  background-color: rgba(245, 245, 219, 0.4);
`;

const Header = styled.section`
  display: flex;
  justify-content: space-between;
  padding: 100px 50px 0;
  color: rgba(0, 0, 0, 0.4);
  font-size: 15px;
`;

const WrapMain = styled.section`
  padding-top: 80px;
  span {
    font-size: 14px;
  }
`;

const Question = styled.div`
  text-align: center;
  font-size: 25px;

  h1 {
    .emphasis {
      width: 380px;
      padding: 35px;
      border-radius: 50px;
      color: #fff;
      background-color: rgba(191, 204, 0, 0.8);
      font-weight: bold;
      line-height: 30px;
    }
  }
`;

const Answer = styled.div`
  ${flexCenter};
  flex-direction: column;
  padding-top: 80px;

  p {
    color: rgba(0, 0, 0, 0.4);
    padding: 20px 0;
    font-size: 24px;
  }
`;

const Button = styled.button`
  width: 360px;
  padding: 30px 50px;
  border: 2px solid rgba(0, 0, 0, 0.08);
  border-radius: 50px;
  font-size: 16px;
  outline-style: none;
  font-weight: bold;

  &:hover {
    ${boxShadow}
    cursor: pointer;
    transform: scale(1.05);
    transition: 0.2s;
    color: #fff;
    background-color: rgba(191, 204, 0, 0.7);
  }
`;

const WrapBottomButton = styled.button`
  display: flex;
  border: none;
`;

const SkipButton = styled.button`
  width: 100px;
  margin: 0 20px;
  padding: 10px 20px;
  margin-top: 50px;
  border: none;
  border-radius: 50px;
  background-color: #becc01;
  color: #fff;
  font-size: 14px;
  font-weight: 500px;
  outline-style: none;

  &:hover {
    ${boxShadow}
    cursor: pointer;
    transform: scale(1.1);
    transition: 0.1s;
  }
`;

const Retest = styled(SkipButton.withComponent("button"))``;
